  `intoinfo` char(50) DEFAULT '' COMMENT '转入账户',
  `outtoinfo` char(50) DEFAULT '' COMMENT '转出账户',
  `money` double DEFAULT NULL COMMENT '金额',
  `paytype` char(50) DEFAULT '' COMMENT '支付方式',
  `time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '时间',
  `people` char(50) DEFAULT '' COMMENT '相关人',
  `remark` char(200) DEFAULT '' COMMENT '备注',
  `remain` char(50) DEFAULT '' COMMENT '保留',
  `bidid` int(11) DEFAULT NULL COMMENT '标id',
  `userid` int(11) DEFAULT NULL COMMENT '用户id',
  `intoacc` double DEFAULT NULL COMMENT '转入账户信息',
  `outacc` double DEFAULT NULL COMMENT '转出账户信息',
  PRIMARY KEY (`recid`)
) ENGINE=InnoDB AUTO_INCREMENT=152 DEFAULT CHARSET=utf8;

